class Toast {
    constructor(t) {
        this._title = !1 !== t.title && (t.title || "Title"), this._text = t.text || "Message...",
        this._theme = t.theme || "default", this._autohide = t.autohide && !0, this._interval = +t.interval || 5e3,
        this._create(), this._el.addEventListener("click", t => {
            t.target.classList.contains("toast__close") &&
            this._hide()
        }), this._show()
    } _show() {
        this._el.classList.add("toast_showing"), this._el.classList.add("toast_show"),
        window.setTimeout(() => { this._el.classList.remove("toast_showing") }),
        this._autohide && setTimeout(() => { this._hide() }, this._interval);
        
        var height = window.screen.height * 0.88;
        var res = document.querySelector('.toast-container');
        var height_ = 0;
        var res_toast = document.querySelectorAll(".toast");

        var length_mas = res_toast.length;
        
        for(var i=0;i<length_mas;i++)
            height_ += res_toast[i].offsetHeight;

        res.style.top = height - (8*Math.max(0, length_mas-1) + height_) + 'px';

    } _hide() {
        this._el.classList.add("toast_showing"), this._el.addEventListener("transitionend", () => {
            this._el.classList.remove("toast_showing"), this._el.classList.remove("toast_show"), this._el.remove()
            
            var height = window.screen.height * 0.88;
            var res = document.querySelector('.toast-container');
            var height_ = 0;
            var res_toast = document.querySelectorAll(".toast");
            var length_mas = res_toast.length;
            
            
            for(var i=0;i<length_mas;i++)
            {
                height_ += res_toast[i].offsetHeight;
            }
            
            res.style.top = height - (8*Math.max(0, length_mas-1) + height_) + 'px';
        }, { once: !0 });
        const t = new CustomEvent("hide.toast", { detail: { target: this._el } });
        document.dispatchEvent(t);
        
        //  Add sessionStorage

        let now = new Date();
        
        var array_toast = [];
        var toast_block = JSON.parse(sessionStorage.getItem('toast'));
        if(toast_block!=null){
            for(var i=0;i<toast_block.length;i++){
                if(now.getTime() - toast_block[i]['time_create'] < toast_block[i]['time_live']){
                    array_toast.push({'body':toast_block[i]['body'],
                                    'title':toast_block[i]['title'],
                                    'header': toast_block[i]['header'],
                                    'time_create': toast_block[i]['time_create'],
                                    'time_live': (toast_block[i]['time_create'] + toast_block[i]['time_live']) - now.getTime()});
                }
            }
        }

        sessionStorage.setItem('toast', JSON.stringify(array_toast));

    } _create() {
        const t = document.createElement("div");
        t.classList.add("toast"),
            t.classList.add(`toast_${this._theme}`);
        let e = '{header}<div class="toast__body"></div><button class="toast__close" type="button"></button>';
        const s = !1 === this._title ? "" : '<div class="toast__header"></div>';
        if (e = e.replace("{header}", s), t.innerHTML = e, this._title ? t.querySelector(".toast__header").textContent = this._title : t.classList.add("toast_message"),
            t.querySelector(".toast__body").textContent = this._text, this._el = t, !document.querySelector(".toast-container")) 
            { 
                const t = document.createElement("div"); 
                t.classList.add("toast-container"), 
                document.body.prepend(t) 
        }
        
        document.querySelector(".toast-container").append(this._el);

        //  Add sessionStorage
        // var toast_container = document.getElementsByClassName('toast-container')[0];
        // var header = toast_container.getElementsByClassName('toast__header');
        // var toast_body = toast_container.getElementsByClassName('toast__body');
        let now = new Date();
        
        var toast_block = JSON.parse(sessionStorage.getItem('toast'));
        var array_toast = [];
        if(toast_block!=null){
            for(var i=0;i<toast_block.length;i++){
                if(now.getTime() - toast_block[i]['time_create'] < toast_block[i]['time_live']){
                    array_toast.push({'body':toast_block[i]['body'],
                                    'title':toast_block[i]['title'],
                                    'header': toast_block[i]['header'],
                                    'time_create': toast_block[i]['time_create'],
                                    'time_live': this._interval});
                }
            }
        }
        if(this._interval == 10000){
            array_toast.push({'body': this._text,
                'title': this._title,
                'header': this._theme,
                'time_create': now.getTime(),
                'time_live': this._interval});
        }


        sessionStorage.setItem('toast', JSON.stringify(array_toast));

    }
}